s1 = ''
s2 = ''
Pu = ['A', 'G']
Pi = ['C', 'T']
tst = 0
tsv = 0

if len(s1) == len(s2):
    for i in range(len(s1)):
        if s1[i] != s2[i]:
            if (s1[i] in Pu and s2[i] in Pu) or (s1[i] in Pi and s2[i] in Pi):
                tst +=1
            else:
                tsv +=1

print(tst/tsv)